package ch02.sec02;

//p43 실습하기
public class IntegerLiteralExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int var1 = 0b1011; // 2진수
		int var2 = 0206; // 8진수
		int var3 = 365; // 2진수
		int var4 = 0xB3; // 2진수
		
		System.out.println(var1);
		System.out.println(var2);
		System.out.println(var3);
		System.out.println(var4);
	}

}
